    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        FachadaDBAccess access = new FachadaDBAccess();
        try
        {
            if(access.isValidUser(jTextField3.getText(), jPasswordField1.getText()))
            {
                new SuccessfulLogin().setVisible(true);
            }
            else
            {
                new WrongUser().setVisible(true);
            }
        }catch (Exception ex)
        {
            new WrongUser().setVisible(true);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

